INSERT INTO `cfg_award_event` VALUES (1, 1, 0, '人鱼唱晚', '好优美的歌声，那不是人鱼唱晚吗？', '人鱼羞涩的为你这个路过的陌生人吟唱了一曲后，转身潜入了海底……', 0, 0, '[{t:13,v:11},{t:7,n:9999},{t:5,n:9999},{t:4,n:10},{t:27,v:6,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (2, 1, 0, '失落的文明', '“报告船长！我们发现了一个很古老的沉船，并打捞了一些古董上来！”', '“报告船长！这是一座远古的神殿，我们打捞了一些古董上来！”', 3, 0, '[{t:5,n:-5},{t:1,n:100000},{t:4,n:10},{t:27,v:6,n:2}]', NULL);
INSERT INTO `cfg_award_event` VALUES (3, 1, 0, '黑胡子的袭击', '这个带刀疤的骷髅海贼旗，是黑胡子！！！', '“竟然碰到了臭名昭著的黑胡子！我和你们拼了！”', 5, 1, '[{t:13,v:12},{t:7,n:20},{t:1,n:50000},{t:4,n:20},{t:27,v:6,n:1}]|[{t:101,n:-10},{t:7,n:-5},{t:4,n:5},{t:27,v:6,n:1}]', '5000');
INSERT INTO `cfg_award_event` VALUES (4, 1, 0, '友善的导航', '那片海域有微光闪烁，像是有什么神秘的生物在跃动！', '从未见过的小精灵们纷纷跳出海面，环绕在船只周围，好像要带领你去什么地方.', 0, 0, '[{t:4,n:10},{t:27,v:6,n:1},{t:30},{t:13,v:13}]', NULL);
INSERT INTO `cfg_award_event` VALUES (5, 1, 0, '海洋新探索', '远处的那生物是海兽么，还是别的什么…图鉴中没有记载啊…', '“报告船长！我们发现了一群奇怪的生物！”', 20, 0, '[{t:5,n:-5},{t:4,n:8},{t:27,v:5,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (6, 1, 0, '意外收获', '咦？那是什么呢？好像是一艘事故船', '太好了，有些东西还没有损坏，带上船吧.', 16, 0, '[{t:1,n:1000},{t:4,n:5},{t:5,n:-5},{t:27,v:5,n:2}]', NULL);
INSERT INTO `cfg_award_event` VALUES (7, 1, 0, '广结善缘', '一个很漂亮的瓶子，里面似乎有张写了些东西的纸。是谁传递的呢？', '纸上记录着一位船长的信息，不去结交一下么？', 10, 0, '[{t:4,n:10},{t:13,v:10},{t:27,v:4,n:2},{t:31}]', NULL);
INSERT INTO `cfg_award_event` VALUES (8, 1, 0, '鱼群迁徙', '船只周围有一大群的鱼在游动，好壮观呀！鲜美的鱼群，可以捕捞吗？', '捕捞上来好多鱼，这下子这几天可以加餐了！', 15, 0, '[{t:5,n:-5},{t:7,n:9999},{t:4,n:5},{t:24,v:154,n:2},{t:27,v:4,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (9, 1, 0, '天天好心情 ', '刚下完雨，微湿的天气让人很舒服.', '站在甲板上，看到远方的一道彩虹，顿时觉得心情舒爽，今天的航行一定很顺利！', 10, 0, '[{t:4,n:5},{t:5,n:30},{t:27,v:5,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (10, 1, 0, '巧遇老船长', '隔世的小岛，破损的船只，像是有人居住在此……', '这个人穿着船长的衣服，难道他是隐居在这里的老船长吗？', 18, 0, '[{t:1,n:-1000},{t:5,n:-5},{t:4,n:15},{t:27,v:6,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (11, 1, 0, '海上狂欢', '那边的舰队像是在举办什么庆典活动，不去看看么？', '海上的生活也不会觉得闷，暂时放下烦恼，一起来参加化妆Party吧！', 15, 0, '[{t:7,n:-5},{t:1,n:-1000},{t:5,n:50},{t:4,n:15},{t:27,v:4,n:3}]', NULL);
INSERT INTO `cfg_award_event` VALUES (12, 1, 0, '沉船碎片', '远处发现一艘沉船，像是触礁沉没的，总觉得里面藏着点东西.', '今天的打捞的成果不错，发现一些有用的碎片', 35, 0, '[{t:5,n:-5},{t:4,n:5},{t:7,n:30},{t:27,v:4,n:2}]', NULL);
INSERT INTO `cfg_award_event` VALUES (13, 1, 0, '海上的娱乐', '神秘的娱乐舰队出现了，这帮专门为路过船只制造欢乐的家伙们真是有意思…', '没有活动的时候容易无精打采，来点刺激的游戏吧！', 15, 0, '[{t:5,n:-20},{t:7,n:-5},{t:4,n:5},{t:27,v:4,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (14, 1, 0, '世外桃源', '船长，前方迷雾深处发现一座未被记载在地图上的小岛，需要去探索一下么？', '还没有开发过的小岛？！多宝贵的资源啊！', 15, 0, '[{t:5,n:-10},{t:4,n:20},{t:7,n:30},{t:27,v:5,n:1},{t:24,v:11,n:5}]', NULL);
INSERT INTO `cfg_award_event` VALUES (15, 1, 0, '上古神兽', '这个巨大的乌龟是神话中的上古神兽吗？！天啊……', '这只巨龟壳上还真是藏着有趣的东西…', 5, 0, '[{t:4,n:15},{t:27,v:7,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (16, 1, 0, '巨型章鱼', '海洋猎袭者，巨型章鱼…可怕的东西出现了，那一定是它的触手，我不会认错的…', '“我们的船只遭受巨型章鱼袭击！！全体参与战斗，不能让它破坏掉船体！”', 15, 2, '[{t:5,n:-5},{t:7,n:50},{t:4,n:10},{t:1,n:5000},{t:27,v:5,n:1}]|[{t:5,n:-5},{t:7,n:-5},{t:4,n:5},{t:27,v:4,n:2}]', '5000');
INSERT INTO `cfg_award_event` VALUES (17, 1, 0, '那是什么', '远处一点白色的东西是什么？居然会是UFO！哦。。是辐射吗？我觉得好难受。。', '哦...是辐射吗？我觉得好难受…这奇怪的射线…', 13, 0, '[{t:5,n:-10},{t:7,n:-5},{t:4,n:5},{t:17,n:1},{t:27,v:6,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (18, 1, 0, '海盗盛宴', '那几艘船，像是海盗船！怎么办，要绕另一条安全的航线么，船长？', '我的钱和货物啊…该死的海盗！', 15, 0, '[{t:7,n:-5},{t:101,n:-5},{t:27,v:5,n:2}]', NULL);
INSERT INTO `cfg_award_event` VALUES (19, 1, 0, '酒后航行', '船长，这次在港口酒买的比较多，要不要顺便办个船员酒会？', '嗝儿...喝多了，不能再行进了..让我休息下', 18, 0, '[{t:5,n:-10},{t:7,n:-5},{t:27,v:4,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (20, 1, 0, '天气最重要', '那片海域像是有雷暴雨，阴沉沉的…', '这样的天气简直是对行船的诅咒！船员们，让我们和天气抗争吧！', 20, 0, '[{t:5,n:-10},{t:4,n:10},{t:27,v:5,n:1}]', NULL);
INSERT INTO `cfg_award_event` VALUES (21, 1, 0, '灾难降临', '最近经常有船只在那片海域出事，总之大家小心一点！', '船怎么摇晃的这么厉害？！不好，竟然触礁了！赶快处理！', 18, 0, '[{t:5,n:-15},{t:1,n:-2000},{t:4,n:5},{t:27,v:4,n:2},{t:16,n:1}]', NULL);
