INSERT INTO `cfg_commander` VALUES (9, '巴巴罗萨', 1, 0, 5, '穷凶极恶的地中海巴巴里海盗首领，有着压倒性的暴力及深谋远虑的军事头脑，对各国海军来说，海雷丁·巴巴罗萨，就等同是一个“恐怖”的代名词。', 1388, 248, 330, 65, 210, 44, 105, 20, 72, 16, 'G', 30, 'G', 30, 'G', 30, '92', 100, '140,164', 25, '134', 100, 18, 0, 0);
INSERT INTO `cfg_commander` VALUES (10, '爱德华蒂奇', 1, 0, 5, '绰号黑胡子，世界航海史上最臭名昭彰的海盗之一，身为海盗却指挥着有40门火炮的“安妮女王复仇号”挑战英国皇家海军。', 1278, 231, 360, 68, 180, 42, 145, 28, 64, 14, 'G', 30, 'G', 30, 'G', 30, '95', 100, '152,164', 25, '137', 100, 19, 0, 0);
INSERT INTO `cfg_commander` VALUES (11, '麦哲伦', 3, 0, 5, '葡萄牙人,为西班牙政府效力探险。1519年 ~1521年率领船队完成世界上首次环球航行，途中不幸死于菲律宾。', 1200, 230, 135, 27, 135, 27, 345, 70, 160, 30, 'E', 30, 'E', 30, 'E', 30, '1', 100, '135', 25, '13', 100, 20, 0, 0);
INSERT INTO `cfg_commander` VALUES (12, '哥伦布', 2, 0, 5, '生于意大利，却成为了西班牙最伟大的航海家。在西班牙国王支持下，先后4次出海远航，发现美洲大陆，并证明了大地球形说的正确性。', 1575, 280, 250, 52, 330, 60, 195, 31, 25, 4, 'H', 30, 'H', 30, 'H', 30, '116', 100, '128', 25, '143', 100, 21, 0, 0);
INSERT INTO `cfg_commander` VALUES (13, '郑和', 2, 0, 5, '原名马三宝，为明朝立下赫赫战功，官至四品。永乐年间，带领庞大船队七下西洋，最远踏足红海及非洲东部，其航海规模、技术、航程皆为当时世界领先。', 1470, 290, 260, 50, 310, 63, 150, 36, 30, 1, 'H', 30, 'H', 30, 'H', 30, '155', 100, '122', 25, '101', 100, 22, 0, 0);
INSERT INTO `cfg_commander` VALUES (14, '达芬奇', 4, 0, 5, '列奥纳多·达·芬奇（1452—1519），意大利文艺复兴三杰之一，一个真正横跨艺术科学哲学音乐文学领域的天才。', 975, 185, 95, 17, 200, 37, 205, 44, 375, 72, 'F', 30, 'F', 30, 'F', 30, '7', 100, '138', 25, '37', 100, 23, 0, 0);
INSERT INTO `cfg_commander` VALUES (15, '李华梅', 2, 0, 5, '大明帝国海军的女将军。虽在军中长大但考虑问题是从民众角度出发。父亲是地方舰队舰长，在与倭寇作战中阵亡。', 1375, 275, 238, 48, 275, 55, 175, 35, 25, 5, 'H', 30, 'H', 30, 'H', 30, '98', 100, 'H', 25, '107', 100, 24, 0, 0);
INSERT INTO `cfg_commander` VALUES (16, '杏太郎', 1, 0, 5, '日本人，九州岛一豪族后裔，一族人被敌人袭击，父亲切腹自杀。为了复仇而流浪于海上。', 1188, 238, 300, 60, 200, 40, 125, 25, 75, 15, 'G', 30, 'G', 30, 'G', 30, '104', 100, 'G', 25, '140', 100, 25, 0, 0);
INSERT INTO `cfg_commander` VALUES (17, '赫德拉姆', 2, 0, 5, '生于瑞典。从少年时代开始，他就显露出非凡的领导才能，是常被寄予厚望的那种天才领袖。', 1360, 277, 245, 50, 290, 54, 140, 30, 26, 5, 'H', 30, 'H', 30, 'H', 30, '110', 100, 'H', 25, '155', 100, 26, 0, 0);
INSERT INTO `cfg_commander` VALUES (18, '杰克斯帕罗', 1, 0, 5, '“黑珍珠号”海盗船的船长，一度是加勒比海海盗王。热衷冒险，幽默而又放荡不羁。', 1150, 235, 315, 62, 205, 38, 115, 22, 70, 16, 'G', 30, 'G', 30, 'G', 30, '89', 100, 'G', 25, '152', 100, 27, 0, 0);
INSERT INTO `cfg_commander` VALUES (19, '蒂雅', 2, 0, 5, '蒂雅·瓦曼·恰斯卡--继承统治印加帝国一个地方王族血统的混血儿，受芬·布兰科提督的提拔，委以一支舰队，让其在美洲贸易。', 1400, 273, 235, 47, 270, 55, 200, 32, 24, 5, 'H', 30, 'H', 30, 'H', 30, '119', 100, 'H', 25, '143', 100, 28, 0, 0);
INSERT INTO `cfg_commander` VALUES (20, '丽璐阿歌特', 2, 0, 5, '从小开始过着自食其力的生活，慢慢扩大到经营运输业。性格开朗、思想乐观，能理解别人心中的痛苦，是她潜在的魅力。', 1350, 280, 243, 51, 285, 57, 160, 31, 28, 3, 'H', 30, 'H', 30, 'H', 30, '125', 100, 'H', 25, '155', 100, 29, 0, 0);
INSERT INTO `cfg_commander` VALUES (21, '亚伯拉罕', 3, 0, 5, '亚伯拉罕.易文.伍丁，阿拉伯大富翁的后代，表面上是为了兴趣收集古董，实际上是为了追查自己家族的身世秘密而开始世界航海旅行。', 1125, 225, 125, 25, 125, 25, 325, 65, 188, 38, 'E', 30, 'E', 30, 'E', 30, '22', 100, 'E', 25, '158,170', 100, 30, 0, 0);
INSERT INTO `cfg_commander` VALUES (22, '维多利亚', 4, 0, 5, '欧洲名门奥赛罗家族的小姐，有被誉为“社交之花”的美貌，心性善良而又热衷于艺术品的发掘和保护。', 895, 180, 80, 16, 185, 34, 215, 43, 335, 64, 'F', 30, 'F', 30, 'F', 30, '83', 100, 'F', 25, '173', 100, 31, 0, 0);
INSERT INTO `cfg_commander` VALUES (23, '拉斐尔', 3, 0, 5, '葡萄牙中产阶级出身。一个从小被宠着长大却向往骑士般的传奇和冒险的开朗少年。\r\n', 1145, 227, 130, 24, 120, 24, 330, 66, 180, 35, 'E', 30, 'E', 30, 'E', 30, '28,31', 100, 'E', 25, '146', 100, 32, 0, 0);
INSERT INTO `cfg_commander` VALUES (24, '阿一', 1, 0, 5, '一个和哥哥阿吉相依为命的孤儿，在杭州捕鱼为生。这个明朝末年的小人物却因为一次偶然的机会成为了改变中日历史的关键人物。', 1220, 230, 285, 59, 208, 45, 110, 22, 65, 14, 'G', 30, 'G', 30, 'G', 30, 'G', 100, '131', 25, '164', 100, 33, 0, 0);
INSERT INTO `cfg_commander` VALUES (25, '朱利安', 4, 0, 5, '这位有着花一样美貌的男子是法国宫廷的宠儿，也是马赛最著名的寻宝家，拥有超一流的古董鉴赏眼光。', 875, 175, 75, 15, 175, 35, 225, 45, 325, 65, 'F', 30, 'F', 30, 'F', 30, '77', 100, 'F', 25, '161', 100, 34, 0, 0);
