INSERT INTO `cfg_mission` VALUES (10, 9, '竞技场的新人', '一年一度的竞技场七海争霸赛也开始了,不去报个名么,如果想成为世界上叫得出名字的船长,这是个很好的跳板哦~', '战斗->选择玩家->挑战', '完成一次竞技场战斗', 6, 8, '经验+100,绿水晶+200', '4=100,52=200', 80, 1);
INSERT INTO `cfg_mission` VALUES (13, 12, '船长们的友谊', '已经和你混的脸熟的守卫，今天也拉你陪他喝酒“你就不认识些其他舰队的船长么，这片大海可不会软弱到被一个独行船长征服~嗝~”', '好友->谁在玩->选择玩家->加好友', '添加一个好友', 11, 13, '经验+170,紫水晶+200', '4=170,51=200', 1, 1);
INSERT INTO `cfg_mission` VALUES (15, 14, '实战训练', '码头上经常停靠着其他舰队，考虑到今后海洋上充满了海盗、怪物、敌国舰队这些强有力的威胁，现在和这些船长切磋一番是个提升自己的不错选择。', '码头舰队->选择玩家->切磋', '与一名其他玩家切磋', 12, 16, '经验+200,金币+5000', '4=200,1=5000', 34, 1);
INSERT INTO `cfg_mission` VALUES (19, 18, '商人与城市', '\"去王宫完成一次商业投资!商人和城市的关系就如同鱼与池塘.城市越发达,商人才能更赚钱!\"', '王宫->输入商业投资份额->商业投资', '进行一次商业投资', 14, 19, '经验+500,金币+5000', '4=500,1=5000', 25, 1);
INSERT INTO `cfg_mission` VALUES (20, 19, '谁的码头？！', '码头的闹事船长越来越多，城主已经有点受不了了，特此委托你去教训几个带头的船长，让他们以后安分一点！', '码头舰队->选择玩家->切磋', '码头切磋获胜3次', 13, 17, '经验+230,金币+5000', '4=230,1=5000', 157, 3);
INSERT INTO `cfg_mission` VALUES (21, 20, '新面貌', '“你这张脸我已经看厌了！快来我这里理个发修正一下！”你被理发店的大妈强行拉入了店中。', '个人信息->头像设置->选择', '更换一次头像', 4, 7, '经验+30,金币+2000', '4=30,1=2000', 142, 1);
INSERT INTO `cfg_mission` VALUES (22, 21, '勇敢的初心者', '说到公平切磋就不得不提到竞技场了啊,在这个神圣的舞台上战胜各种对手吧!', '战斗->选择玩家->挑战', '竞技场中挑战任意玩家2次', 11, 15, '经验+180,绿水晶+200', '4=180,52=200', 158, 2);
INSERT INTO `cfg_mission` VALUES (31, 30, '发展军事', '“商业投资再多，城市军事力量不强还是要受欺负啊~ 于是你准备军事投资捐献多少？”—征税官一本正经的问道', '王宫->输入军事投资份额->军事投资', '进行一次军事投资', 16, 21, '经验+600,金币+5000', '4=600,1=5000', 26, 1);
INSERT INTO `cfg_mission` VALUES (32, 31, '留下买路财', '“如果看见十分不顺眼的人，就去劫掠他，大不了掉点声望，不过可以获得很多钱哦~咱们关系这么铁，我会当做没看见的~”—码头长发守卫', '码头舰队->选择玩家->劫掠', '对其他玩家进行一次劫掠', 19, 30, '经验+1000,金币+10000', '4=1000,1=10000', 59, 1);
INSERT INTO `cfg_mission` VALUES (36, 35, '劫掠者', '总觉得其他国家的人很碍事吧!反正不是一个国家的,放心的去劫掠对方把!', '劫掠5名异国玩家', '劫掠5次其他国家的舰队并成功', 30, 40, '经验+2000,金币+50000', '4=2000,1=50000', 99, 1);
INSERT INTO `cfg_mission` VALUES (39, 38, '大人物!', '航海的世界中,只有一种人才真正的被称做大人物!那就是\"城主\"去完全的占有一座城市,成为领主吧!', '个人独占一座城市,需要投资达到100%', '独占一座城市', 35, 70, '经验+2500,金币+50000', '4=2500,1=50000', 28, 1);
INSERT INTO `cfg_mission` VALUES (40, 39, '周游世界', '去在这个世界每一个城市都留下你的脚印,感受不同地域的风土人情,尝遍世界美食!根据地图提示去自由的航行吧.', '发现了所有的城市', '发现了所有的城市', 35, 70, '经验+3000,万能升阶卡*1', '4=3000,41=1', 29, 1);
INSERT INTO `cfg_mission` VALUES (41, 40, '富甲天下', '\"不是每个商人都能成功.对利润的渴望是一种天赋,是生来就流淌在你血液中的因子!让全世界的人们都看到你的商业天赋!赚钱,赚钱,再赚钱!进入财富榜前十!\"', '进入财富榜前10', '进入财富榜前10', 35, 70, '经验+3000,万能升阶卡*2', '4=3000,41=2', 30, 1);
INSERT INTO `cfg_mission` VALUES (42, 41, '纵横四海', '\"每一名船长的生活都交织着血与金!而\"\"七海之王\"\"无疑是他们中真正的佼佼者!你敢试试么？去战斗,去掠夺吧!进入\"\"战斗榜\"\"前十位就是你的目标!\"', '进入战斗榜前10', '进入战斗榜前10', 35, 70, '经验+3000,万能升阶卡*2', '4=3000,41=2', 31, 1);
INSERT INTO `cfg_mission` VALUES (44, 0, '探险之路（一）', '郊外的土著居民最近发来了求救信，希望冒险者能帮他们赶走强盗，他们承诺了一大笔丰厚的报酬', '', '完成探险【一起教训勒索土著的强盗】', 2, 4, '经验+8,金币+1000', '4=8,1=1000', 166, 1);
INSERT INTO `cfg_mission` VALUES (45, 0, '战斗之路（一）', '不来这里挑战试试么，这里的敌人每次战胜后都会变得更强哦~当然，胜利者的报酬是绝对丰厚的！', '', '完成战斗探险【#NAME#】', 2, 5, '经验+15,白银水手*1,黄金水手*1', '4=15,47=1,48=1', 167, 1);
INSERT INTO `cfg_mission` VALUES (46, 0, '探险之路（二）', '“这帮该死的恶棍，每次来喝酒都动手动脚的…拜托你帮帮我，去狠狠教训他们一顿！”——愤怒的街边女子', '', '完成探险【赶跑欺负酒吧女郎的恶棍】', 3, 6, '经验+5,4☆克里斯蒂娜+1', '4=5,80=1', 168, 1);
INSERT INTO `cfg_mission` VALUES (47, 0, '探险之路（三）', '“每次都是！每次都是眼睁睁的看着那群小偷逃走，真希望有人能帮我逮住他们，我承诺会给你满意的报酬！”——疲惫的守卫', '', '完成探险【勇擒在市场偷钱的坏蛋】', 3, 7, '经验+30,星钻+50', '4=30,2=50', 169, 1);
INSERT INTO `cfg_mission` VALUES (50, 0, '新的世界！', '这片海域对你来说已经像是后花园一样了，去新的海域冒险吧，我在世界地图上已经给你标记了新的区域城市的位置，前往那里吧！', '', '等级提升到10级并前往#NAME#（已在地图上用星状标记标出）', 10, 12, '经验+150,金币+3000', '4=150,1=3000', 172, 1);
INSERT INTO `cfg_mission` VALUES (52, 0, '角斗士', '说到公平切磋就不得不提到竞技场了啊,在这个神圣的舞台上战胜各种对手吧!', '', '竞技场中取得2场胜利', 12, 17, '经验+200,绿水晶+400', '4=200,52=400', 174, 2);
INSERT INTO `cfg_mission` VALUES (53, 0, '新的日常!', '“每天的公告栏都会张贴出一天的全部日常任务，如果全部完成的话，城主会奖励一个超超超超级稀有的宝箱哦！！！”消息来自议论纷纷的群众', '', '完成今日任务板上的全部日常任务', 15, 20, '经验+550,金币+5000', '4=550,1=5000', 91, 1);
INSERT INTO `cfg_mission` VALUES (55, 0, '强化装备', '装备是可以通过强化提升性能，但需要消耗一定量的金币哦！去把我上次送你的火炮强化到10级吧——城主的信', '', '将旗舰上的武器强化至10级', 7, 9, '经验+30,金币+3000', '4=30,1=3000', 155, 1);
INSERT INTO `cfg_mission` VALUES (57, 0, '战斗之路（二）', '“啊啊，又在这里见到你了，您总是火力充沛呢！这次也要去挑战强敌了么，祝你好运！”——旅行商人', '', '完成战斗探险【#NAME#】', 4, 6, '经验+35,紫水晶+200', '4=35,51=200', 180, 1);
INSERT INTO `cfg_mission` VALUES (58, 0, '探险之路（四）', '\"野外又出现了巨型章鱼袭击人类的事情，可最近几个月加班搜索却又完全没能找到他的踪迹，这可怎么办啊…..\"——忧心的守卫', '完成支线探险【勇救被章鱼缠住的男孩】', '完成支线探险【勇救被章鱼缠住的男孩】', 5, 7, '经验+50,金币+3000', '4=50,1=3000', 181, 1);
INSERT INTO `cfg_mission` VALUES (59, 0, '贸易委托', '交易所的老板希望有人能帮他运送些货物到附近城市卖掉,快去他那里采购商品吧。', '购买商品并航行到新城市，出售给交易所', '购买商品并航行到新城市，出售给交易所', 5, 8, '经验+50,黄水晶+200', '4=50,53=200', 182, 1);
INSERT INTO `cfg_mission` VALUES (60, 0, '商人的方式', '交易所的老板希望你能再帮他去运送并出售一次货物，上次你干净利落的交易方式让他十分满意。', '配备上会计师后，再去完成一次贸易', '配备上会计师后，再去完成一次贸易', 5, 8, '经验+80,黄水晶+200', '4=80,53=200', 183, 1);
INSERT INTO `cfg_mission` VALUES (61, 0, '探险之路（五）', '\"不….不要…玩….玩…玩火！啊，我….刚说完….怎么就有…..有火灾报….报告了！！\"——困扰的守卫', '完成支线探险【扑灭失火的船舱】', '完成支线探险【扑灭失火的船舱】', 6, 9, '经验+80,金币+3000', '4=80,1=3000', 184, 1);
INSERT INTO `cfg_mission` VALUES (62, 0, '战斗之路（三）', '“哇，是你，通往新区域的道路被人挡住了，求你帮我去赶走他们,我会给你水晶作为谢礼的！”——水晶学者', '完成战斗探险【#NAME#】', '完成战斗探险【#NAME#】', 7, 10, '经验+80,红水晶+100,黄水晶+100,绿水晶+100', '4=80,54=100,53=100,52=100', 185, 1);
INSERT INTO `cfg_mission` VALUES (63, 0, '探险之路（六）', '“呜呜….那群海盗太凶残了，现在我每天凌晨4点偷偷出海捕鱼都会被他们发现，这样下去可怎么办啊...”哭泣的渔民', '完成支线探险【追捕打劫渔船的海盗】', '完成支线探险【追捕打劫渔船的海盗】', 7, 10, '经验+70,金币+3000', '4=70,1=3000', 186, 1);
INSERT INTO `cfg_mission` VALUES (64, 0, '战斗之路（四）', '“赞美上帝，你又来了，我们还是有很多棘手的任务需要您的帮助！”——如释重负的守卫', '完成战斗探险【#NAME#】', '完成战斗探险【#NAME#】', 8, 11, '经验+50,万能升阶卡*1,精英水手*1', '4=50,41=1,46=1', 187, 1);
INSERT INTO `cfg_mission` VALUES (65, 0, '世界的秘密（一）', '每个区域都有一个最后的主线探险，这是这个区域中奖励最丰厚的探险点，千万千万不要错过哦~', '完成主线探险【第一章】', '完成主线探险【第一章】', 8, 11, '经验+100,红水晶+200', '4=100,54=100', 156, 1);
INSERT INTO `cfg_mission` VALUES (66, 0, '探险之路（七）', '“呜呜….那群海盗虽然上次被您击退，但过了一段时间回来后更加变本加厉了，完全不给活路啊！”——悲痛欲绝的渔民', '完成支线探险【摆平海盗的报复】', '完成支线探险【摆平海盗的报复】', 10, 13, '经验+170,金币+3000', '4=170,1=3000', 189, 1);
