INSERT INTO `cfg_npc` VALUES (1, 1, 11, '欢迎光临，想让全世界的人听到你说什么不？多去黑市买几个漂流瓶吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (2, 1, 0, '欢迎你啊，帅哥。你想从我这里找点什么乐子？', 0, 70);
INSERT INTO `cfg_npc` VALUES (3, 1, 12, '航海累了，来到酒馆休息聊天是个不错的选择，还能认识很多新朋友呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (4, 1, 12, '同城发言、国家发言和公会发言都是免费的哦～', 0, 70);
INSERT INTO `cfg_npc` VALUES (5, 1, 13, '遇到高兴事情赶紧请在场的喝一杯吧，大家都能回复1点体力', 5, 70);
INSERT INTO `cfg_npc` VALUES (6, 1, 13, '听说在酒馆请喝酒是提高在酒馆所属势力声望的好办法呢', 10, 70);
INSERT INTO `cfg_npc` VALUES (7, 1, 13, '在酒馆请喝酒能提高声望，不过一天最多20点', 10, 70);
INSERT INTO `cfg_npc` VALUES (8, 1, 13, '没事在酒馆混着，运气好碰见请喝酒的就能回体力。不过1小时最多10点。', 10, 70);
INSERT INTO `cfg_npc` VALUES (9, 1, 14, '一支舰队没有副官可万万不行，很多流浪的英雄都滞留在酒馆里，来这招募吧', 5, 70);
INSERT INTO `cfg_npc` VALUES (10, 1, 14, '在酒馆里呆着的英雄一般两小时就会换一拨，记得到时候来看看啊', 5, 70);
INSERT INTO `cfg_npc` VALUES (11, 1, 14, '副官上任后，会分别提升体力上限、回复速度，攻防还有交易、制造的能力！', 5, 70);
INSERT INTO `cfg_npc` VALUES (12, 1, 14, '要是对英雄不满意，只要跟我意思意思刷新一下，我马上帮你引荐一批新的。', 10, 70);
INSERT INTO `cfg_npc` VALUES (13, 1, 14, '旗舰可以配备六个不同职位的副官，尽早配备齐会极大的提升战斗力', 10, 30);
INSERT INTO `cfg_npc` VALUES (14, 1, 14, '副官都有后勤，军事，商业三种属性哦，要记得根据他们的特点分配职务', 5, 70);
INSERT INTO `cfg_npc` VALUES (15, 1, 14, '对副官里的航海长和仓管员来说，后勤就是最重要的属性', 5, 70);
INSERT INTO `cfg_npc` VALUES (16, 1, 14, '水兵长和炮击手是主管攻击和防御的，当然要选战斗属性高的！', 5, 70);
INSERT INTO `cfg_npc` VALUES (17, 1, 14, '要想赚钱多制造东西快，在会计师和工匠师位置上必须放上商业属性高的', 5, 70);
INSERT INTO `cfg_npc` VALUES (18, 1, 14, '副官会分为1星到5星。星级越高意味着副官的总属性点和成长性范围越高', 5, 70);
INSERT INTO `cfg_npc` VALUES (19, 1, 14, '别光迷恋总属性高的副官，全能不如特长！', 15, 70);
INSERT INTO `cfg_npc` VALUES (20, 1, 14, '副官就职后会不断的成长升级，升级增加点数多少就是由成长性决定的', 15, 70);
INSERT INTO `cfg_npc` VALUES (21, 1, 14, '副官到达四星后就会带特殊技能了！这个时候才是最强的！', 15, 70);
INSERT INTO `cfg_npc` VALUES (22, 1, 14, '只要运气好的话就能在我的酒馆里见到那些传说中的四星和五星英雄！', 5, 70);
INSERT INTO `cfg_npc` VALUES (23, 1, 14, '传奇副官意味着强悍的当前，而一些低星级副官很可能具备更高的潜力！', 15, 70);
INSERT INTO `cfg_npc` VALUES (24, 1, 14, '招副官是需要花费金币的，记得带好金币哦，别没金币干着急～', 10, 70);
INSERT INTO `cfg_npc` VALUES (25, 1, 14, '最好的副官？当然是成长高，单项属性高！', 5, 70);
INSERT INTO `cfg_npc` VALUES (26, 1, 14, '招募好副官后一定要记得进入“舰队”去部署好副官哦！', 5, 20);
INSERT INTO `cfg_npc` VALUES (27, 1, 14, '每个人最多有12名替补副官。招募的时候满了的话可以先去舰队解雇掉不合格的。', 15, 70);
INSERT INTO `cfg_npc` VALUES (28, 1, 15, '酒馆就是一个娱乐的地方，可以聊天，幸运乐购，玩骰子！运气好还能赚一笔！', 3, 70);
INSERT INTO `cfg_npc` VALUES (29, 1, 15, '幸运乐购里的物品都是很厉害的装备哦，只用很少量的宝石就有机会得到呢！', 5, 70);
INSERT INTO `cfg_npc` VALUES (30, 1, 15, '幸运乐购只要参加的人数够了就会马上开奖把奖品给予一个幸运儿，会不会是你呢？', 5, 70);
INSERT INTO `cfg_npc` VALUES (31, 1, 15, '累了还能来玩玩骰子，小玩怡情，千万别伤身哦!', 5, 70);
INSERT INTO `cfg_npc` VALUES (32, 1, 15, '所谓幸运乐购就是，每个人出一份钱，买了装备后，把这个装备随机给其中一个人！', 5, 70);
INSERT INTO `cfg_npc` VALUES (33, 2, 21, '船长，你需要花费xxx金币来补给满么？补给不足有可能影响到战斗力', 0, 70);
INSERT INTO `cfg_npc` VALUES (34, 2, 21, '船长，你需要花费xxx金币来补给满么？在海上没有补给会停止前行哦', 0, 70);
INSERT INTO `cfg_npc` VALUES (35, 2, 21, '船长，你需要花费xxx金币来补给满么？听说在海上可以紧急补给，不过很贵', 0, 70);
INSERT INTO `cfg_npc` VALUES (36, 2, 21, '船长，你需要花费xxx金币来补给满么？补给会慢慢回复的，3分钟就能回1%', 0, 70);
INSERT INTO `cfg_npc` VALUES (37, 2, 21, '船长，你需要花费xxx金币来补给满么？补给费用和你的船只、等级有关，越多越贵', 0, 70);
INSERT INTO `cfg_npc` VALUES (38, 2, 21, '船长，你需要花费xxx金币来补给满么？补给低的时候被劫掠很容易失败！', 0, 70);
INSERT INTO `cfg_npc` VALUES (39, 2, 21, '船长，你需要花费xxx金币来补给满么？据说有加快补给回复的副官存在', 10, 70);
INSERT INTO `cfg_npc` VALUES (40, 2, 21, '船长，你需要花费xxx金币来补给满么？你在敌国码头可享受不到补给服务', 5, 70);
INSERT INTO `cfg_npc` VALUES (41, 2, 21, '船长，你需要花费xxx金币来补给满么？到海上没补给只能紧急补给或者等回复了', 5, 70);
INSERT INTO `cfg_npc` VALUES (42, 2, 22, '你可以从码头出海或者与停泊在码头的舰队们切磋。记住，切磋是带彩头的！', 5, 70);
INSERT INTO `cfg_npc` VALUES (43, 2, 22, '下线的时候记得回到自己国家势力的码头，可以减少被人劫掠的机会', 5, 70);
INSERT INTO `cfg_npc` VALUES (44, 2, 22, '劫掠自己国家和同盟的同胞会被减少很多声望的！', 5, 70);
INSERT INTO `cfg_npc` VALUES (45, 2, 22, '在人家码头劫掠很可能会被守城部队给打翻在地！别那么愚蠢！', 10, 70);
INSERT INTO `cfg_npc` VALUES (46, 2, 22, '船长，今天是个出海的好天气，你觉得呢？', 0, 70);
INSERT INTO `cfg_npc` VALUES (47, 2, 23, '带满补给，船员才有斗志呢，挑战对手或者出海航行都心里不慌', 0, 70);
INSERT INTO `cfg_npc` VALUES (48, 2, 23, '每发现一个新城市，我们的体力上限都会增长1哦！', 0, 70);
INSERT INTO `cfg_npc` VALUES (49, 2, 23, '只要我们发现了新城市，就会自动开启航线哦。在出航时点击城市名就能直航到达啦', 0, 20);
INSERT INTO `cfg_npc` VALUES (50, 2, 23, '直航可方便啦，不过消耗的体力会比我们自己航行要多，经验也会少一些呢', 3, 40);
INSERT INTO `cfg_npc` VALUES (51, 2, 23, '出海航行走多远要看船长的运气哦，最远可以一口气航行六天呢', 0, 30);
INSERT INTO `cfg_npc` VALUES (52, 2, 23, '每到一个城市我们都可以去交易所买这个城市的传送卷轴，这样可以随时回来啦', 10, 70);
INSERT INTO `cfg_npc` VALUES (53, 2, 23, '出海也会得到经验哦，不比做任务差，还能一边做贸易，我最喜欢啦', 0, 30);
INSERT INTO `cfg_npc` VALUES (54, 2, 23, '我们的航海路线和你的世界是一样的哦，不信去翻翻GOOGLE地图吧，哈哈', 5, 70);
INSERT INTO `cfg_npc` VALUES (55, 2, 23, '自由出海航行有时候会停泊在海上，能碰到美人鱼也可能会碰到幽灵船……', 0, 50);
INSERT INTO `cfg_npc` VALUES (56, 2, 23, '航海世界这么大，船长你一定要带领我们去征服她好不好！', 0, 70);
INSERT INTO `cfg_npc` VALUES (57, 2, 24, '在码头和其他船长切磋会得到经验哦。当然，胜利了会有更丰厚的金币收获', 0, 60);
INSERT INTO `cfg_npc` VALUES (58, 2, 24, '战斗的胜利之匙就是：更多的旗舰，更好的船，更强的大炮，更好的副官！', 10, 70);
INSERT INTO `cfg_npc` VALUES (59, 2, 24, '要想挑战胜利就要看咱们的攻击力是不是比他们的防御力更强了！', 10, 70);
INSERT INTO `cfg_npc` VALUES (60, 2, 24, '有时候我们会被挑战哦。这个时候只要我们的防御力足够强就没问题', 10, 70);
INSERT INTO `cfg_npc` VALUES (61, 2, 24, '要是我们的舰船和大炮比战斗船只还多就不起作用啦，多余的装备不算数呢', 10, 70);
INSERT INTO `cfg_npc` VALUES (62, 2, 24, '更多的朋友意味着更多的船只，再去舰队里把他们改造成战斗船只就能变强', 10, 70);
INSERT INTO `cfg_npc` VALUES (63, 2, 24, '战斗船只多不一定有用，必须每个船只配上一个好船和一个好武器才厉害', 10, 70);
INSERT INTO `cfg_npc` VALUES (64, 2, 24, '好友默认都是商船哦，没有战斗力的。要战斗厉害必须去舰队改造出更多战斗船只', 10, 70);
INSERT INTO `cfg_npc` VALUES (65, 2, 24, '切磋胜利后我们会赢得对方身上现金的3%，运气好会让我们迅速致富！', 10, 70);
INSERT INTO `cfg_npc` VALUES (66, 2, 24, '我们被其他船队切磋打败后，会丢失身上现金的3%。把巨款存到银行会比较安全！', 10, 70);
INSERT INTO `cfg_npc` VALUES (67, 2, 24, '如果遇到那些讨厌的喜欢劫掠的海盗，我们失败后会损失现金的5%！', 10, 70);
INSERT INTO `cfg_npc` VALUES (68, 2, 24, '更多的好友会有更多的船。但是不同等级会有不同的船只上限。努力升级吧！', 10, 70);
INSERT INTO `cfg_npc` VALUES (69, 2, 24, '听说升到最高级后最多能同时拥有70条船只！那得多壮观啊……', 10, 70);
INSERT INTO `cfg_npc` VALUES (70, 2, 25, '海上某些特殊地点有隐藏的可以探险的副本，里面有各种宝藏呢！', 10, 70);
INSERT INTO `cfg_npc` VALUES (71, 2, 25, '海洋是最神秘的区域，这个世界上仍有一些副本连我都没听说过', 10, 70);
INSERT INTO `cfg_npc` VALUES (72, 2, 25, '每个人每天最多完成5次副本探险哦，所以一定要慎重', 10, 70);
INSERT INTO `cfg_npc` VALUES (73, 2, 25, '传闻在西亚的马斯喀特和佐法尔之间有一个副本：山贼巢穴(15~24级)', 15, 24);
INSERT INTO `cfg_npc` VALUES (74, 2, 25, '传闻东亚的海参威和釜山之间有一个副本：海盗防御战(15~24级)', 15, 24);
INSERT INTO `cfg_npc` VALUES (75, 2, 25, '传闻在南美的巴伊亚和里约热内卢之间有一个副本：野蛮的悬崖(15~24级)', 15, 24);
INSERT INTO `cfg_npc` VALUES (76, 2, 25, '传闻在北欧的维斯比和但泽之间有一个副本：阴森的炼金基地(15~24级)', 15, 24);
INSERT INTO `cfg_npc` VALUES (77, 2, 25, '传闻在南欧的希洪和波尔图之间有一个副本：漂浮的爆炸海贼团(25~34级)', 25, 34);
INSERT INTO `cfg_npc` VALUES (78, 2, 25, '传闻在中美的梅里达和特鲁希略有一个副本：漂浮的爆炸海贼团(25~34级)', 25, 34);
INSERT INTO `cfg_npc` VALUES (79, 2, 25, '传闻在东欧的安科纳和卡利亚里之间有一个副本：黑暗海洋餐厅(25~34级)', 25, 34);
INSERT INTO `cfg_npc` VALUES (80, 2, 25, '传闻在东南亚的巨港和雅加达之间有一个副本：黑暗海洋餐厅(25~34级)', 25, 34);
INSERT INTO `cfg_npc` VALUES (81, 2, 25, '传闻在北非的卡萨布兰卡和拉斯帕尔马斯之间有一个副本：部落的武斗大会(35~44级)', 35, 44);
INSERT INTO `cfg_npc` VALUES (82, 2, 25, '传闻在南亚的果阿和卡利卡特之间有一个副本：原始之森(35~44级)', 35, 44);
INSERT INTO `cfg_npc` VALUES (83, 2, 25, '传闻在西非的卡里比布和开普敦之间有一个副本：百慕大惊魂夜(45~54级)', 45, 54);
INSERT INTO `cfg_npc` VALUES (84, 2, 25, '传闻在东非的摩加迪沙和马林迪之间有一个副本：王国的叛徒(45~54级)', 45, 54);
INSERT INTO `cfg_npc` VALUES (85, 2, 25, '传闻在西非的圣多美和杜阿拉之间有一个副本：乔斯的海上城堡(55~65级)', 55, 64);
INSERT INTO `cfg_npc` VALUES (86, 2, 25, '传闻在南亚的亚齐和占碑之间有一个副本：梅拉的发明工厂(65~70级)', 65, 70);
INSERT INTO `cfg_npc` VALUES (87, 2, 25, '传闻在东非的索法拉和纳塔尔之间有一个副本：乔斯的海上城堡(70级)', 70, 70);
INSERT INTO `cfg_npc` VALUES (88, 2, 25, '传闻在澳洲的岚屿和卡卡杜之间有一个副本：卡兹洛加海底监狱(70级)', 70, 70);
INSERT INTO `cfg_npc` VALUES (89, 2, 25, '传闻在澳洲的芬瑟岛和旺阿努伊之间有一个副本：黑珍珠号(70级)', 70, 70);
INSERT INTO `cfg_npc` VALUES (90, 2, 25, '在副本中很容易就可以得到传奇装备,还不快去挑战!', 0, 70);
INSERT INTO `cfg_npc` VALUES (91, 3, 31, 'Time is money！要想赚钱就得趁快！您有何需求？', 0, 70);
INSERT INTO `cfg_npc` VALUES (92, 3, 31, '商品的进价和卖价是会波动的，每个小时都会变动一次', 3, 70);
INSERT INTO `cfg_npc` VALUES (93, 3, 31, '能进多少船货就要看你有多少条商船。更多的好友就是更多的商船！', 0, 70);
INSERT INTO `cfg_npc` VALUES (94, 3, 31, '低买高卖就是贸易的本质！不过更好的计算会让你盈利更多的', 0, 70);
INSERT INTO `cfg_npc` VALUES (95, 3, 31, '出售的商品如果和这个城市特产是同一个类型的，利润会比较低呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (96, 3, 31, '每次进货要和商人砍价会很累的，一次进货需要消耗体力5', 0, 70);
INSERT INTO `cfg_npc` VALUES (97, 3, 31, '利润越高交易获得经验就会更高！', 0, 70);
INSERT INTO `cfg_npc` VALUES (98, 3, 31, '发展度越高的城市商品等级也会越高，商品价格也更高，我们挣的也就更多', 10, 70);
INSERT INTO `cfg_npc` VALUES (99, 3, 31, '交易所偶尔会出现流行性事件哦！这个时候是大赚一笔的好机会！', 15, 70);
INSERT INTO `cfg_npc` VALUES (100, 3, 31, '要是遇到流行事件：畅销，赶紧买这种商品去销售，利润是平常的好几倍', 15, 70);
INSERT INTO `cfg_npc` VALUES (101, 3, 31, '如果遇到流行事件：滞销，这个商品会卖又多又便宜，赶紧进货拉出去卖吧！', 15, 70);
INSERT INTO `cfg_npc` VALUES (102, 3, 31, '要想赚钱多，加入一个高等级商会，有了公会科技帮助会事半功倍的', 15, 70);
INSERT INTO `cfg_npc` VALUES (103, 3, 31, '想提升利润，在你的旗舰上部署一个具有高商业属性的会计师是王道！', 15, 70);
INSERT INTO `cfg_npc` VALUES (104, 3, 31, '有些会计师在四星后拥有强大的商业技能，能极大提高赚钱效率', 15, 70);
INSERT INTO `cfg_npc` VALUES (105, 3, 31, '声望越高国家爵位越高，在我这就能拿到更低的价格和更多的货物！', 15, 70);
INSERT INTO `cfg_npc` VALUES (106, 3, 31, '如果你在这个城市所属势力中声望是冷漠的，进货的价格和数量都很吃亏', 15, 70);
INSERT INTO `cfg_npc` VALUES (107, 3, 31, '在你敌对的势力下城市内是没办法做生意的，还是躲远点吧', 15, 70);
INSERT INTO `cfg_npc` VALUES (110, 3, 32, '枪杆子里出政权，有人有船有炮才能打天下！', 0, 70);
INSERT INTO `cfg_npc` VALUES (111, 3, 32, '不管是做商人还是做海盗都需要武装自己！更多的好友就是更多的战斗船只！', 0, 70);
INSERT INTO `cfg_npc` VALUES (112, 3, 32, '要是我们的舰船和大炮比战斗船只还多就不起作用啦，多余的装备不算数呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (113, 3, 32, '每个人的舰队都由旗舰和普通舰船组成，只有旗舰才能配备撞角这些配件！', 0, 70);
INSERT INTO `cfg_npc` VALUES (114, 3, 32, '舰队里旗舰之外的普通舰船只能自动配备一个船体和大炮，而且还不能发挥特效', 10, 70);
INSERT INTO `cfg_npc` VALUES (115, 3, 32, '每个人都有一艘能装备配件的旗舰，旗舰的攻防会被放大十倍，所以你懂的！', 4, 70);
INSERT INTO `cfg_npc` VALUES (116, 3, 32, '旗舰的配件除了船体和武器之外，还能配备船帆、撞角、船首像和装甲', 4, 70);
INSERT INTO `cfg_npc` VALUES (117, 3, 32, '四星和五星的装备会有特殊技能，不过只有配备到旗舰上才能发挥作用！', 8, 70);
INSERT INTO `cfg_npc` VALUES (118, 3, 32, '装备一旦到达四星后就会出现特殊技能了，到达五星更会出现第二个技能', 12, 70);
INSERT INTO `cfg_npc` VALUES (119, 3, 32, '传奇装备的特殊技能都是固定的哦，普通装备虽然是随机的却有可能更加的厉害！', 15, 70);
INSERT INTO `cfg_npc` VALUES (120, 3, 32, '看过《加勒比海盗》没？我最喜欢黑胡子的安妮女王复仇号了，真想有一艘', 10, 70);
INSERT INTO `cfg_npc` VALUES (121, 3, 32, '更多的朋友意味着更多的船只，再去舰队里把他们改造成战斗船只挑战就能变强', 4, 70);
INSERT INTO `cfg_npc` VALUES (122, 3, 32, '加好友在未到达个人船只上限的时候会默认增加一艘商船', 5, 70);
INSERT INTO `cfg_npc` VALUES (123, 3, 32, '每个人的船只上限是和个人等级有关的，所以，努力升级吧！', 10, 70);
INSERT INTO `cfg_npc` VALUES (124, 3, 32, '其实更棒的装备都来源于那些工匠大师的产品和副本掉落！', 10, 70);
INSERT INTO `cfg_npc` VALUES (125, 3, 32, '在黑市和我这都能买到制造卷轴，制造后会提升熟练度，希望你成为大师。', 10, 70);
INSERT INTO `cfg_npc` VALUES (126, 3, 32, '四星五星装备上的特殊技能数值本身也会有变化，找到更强的那个吧', 20, 70);
INSERT INTO `cfg_npc` VALUES (127, 3, 32, '旗舰的装备作用毫无疑问是最强的，好好打造自己的最强旗舰吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (128, 3, 32, '船只和大炮都有使用的等级需求哦，所以一定要注意是不是自己可以使用的', 10, 70);
INSERT INTO `cfg_npc` VALUES (129, 3, 32, '把不需要的装备去拍卖所或者交易所卖掉都会带来不少的收益', 10, 70);
INSERT INTO `cfg_npc` VALUES (130, 3, 32, '每到一个城市我们都可以去交易所买这个城市的传送卷轴，这样可以随时回来啦', 15, 70);
INSERT INTO `cfg_npc` VALUES (131, 3, 32, '交易所的道具贩卖中有很多非常有用的道具，使用后可以让你赚钱更多战斗更强', 5, 70);
INSERT INTO `cfg_npc` VALUES (132, 3, 32, '在国战频发的年代，带好己方防线的城市回城卷轴是非常必要的防御手段', 20, 70);
INSERT INTO `cfg_npc` VALUES (133, 4, 40, '你到我这里有何贵干？', 0, 70);
INSERT INTO `cfg_npc` VALUES (134, 4, 40, '当你有足够的经济实力和军事实力就来找我投资吧，我会给你足够的回报！', 10, 70);
INSERT INTO `cfg_npc` VALUES (135, 4, 40, '如果我们城市总的占有度还没到100%，那你每投资一份就可获得1%的占有度！', 10, 70);
INSERT INTO `cfg_npc` VALUES (136, 4, 40, '每天晚上12点我们会根据当时的各城市占有度给所有的股东们分红利！', 10, 70);
INSERT INTO `cfg_npc` VALUES (137, 4, 40, '每投资1份商业，城市发展度增加1；每投资1份军事，城市武装度增加1', 15, 70);
INSERT INTO `cfg_npc` VALUES (138, 4, 40, '城市发展度越高，出售的商品等级会越高，每天的分红也会越多', 15, 70);
INSERT INTO `cfg_npc` VALUES (139, 4, 40, '城市武装度越高，敌人攻击城市的时候就会越困难', 15, 70);
INSERT INTO `cfg_npc` VALUES (140, 4, 40, '如果你够自信的话你可以尝试攻击城市，一旦成功所有股东股份全部清零', 20, 70);
INSERT INTO `cfg_npc` VALUES (141, 4, 40, '如果你在我们城市没有占有度，你攻击城市成功后将直接获得占有度1%', 20, 70);
INSERT INTO `cfg_npc` VALUES (142, 4, 40, '当城市已被唯一一个股东占有100%时，同国航海家可以通过攻击城市来抢占股份', 20, 70);
INSERT INTO `cfg_npc` VALUES (143, 4, 40, '当城市被很多股东共同占有100%时，任何人投资都将不会获得占有度', 20, 70);
INSERT INTO `cfg_npc` VALUES (144, 4, 40, '如果很多人共同占有城市100%时，同国航海家可以通过挑战股东或者攻击城市来抢占股份', 20, 70);
INSERT INTO `cfg_npc` VALUES (145, 4, 40, '攻击城市需要缴纳一定的保证金，所以你需要慎重考虑这个风险大收益大的行为哦', 20, 70);
INSERT INTO `cfg_npc` VALUES (146, 4, 40, '挑战股东胜利后你就获得了通过投资来抢占他股份的权利，要注意：过期不候', 20, 70);
INSERT INTO `cfg_npc` VALUES (147, 4, 40, '如果你和城市占领者属于不同势力，要想占城就只能发动国战了', 20, 70);
INSERT INTO `cfg_npc` VALUES (148, 4, 40, '只有每个国家的七级以上爵位的人才可以发动攻城国战', 20, 70);
INSERT INTO `cfg_npc` VALUES (149, 4, 40, '国家势力覆盖到的城市将极大影响本国国民的收入和活动范围', 20, 70);
INSERT INTO `cfg_npc` VALUES (150, 4, 40, '一些拥有制造高级物品所需的战略性原料城市是兵家必争之地', 20, 70);
INSERT INTO `cfg_npc` VALUES (151, 4, 40, '完全占有一个城市，成为城市\"领主\"是航海世界的最高荣誉之一！', 20, 70);
INSERT INTO `cfg_npc` VALUES (152, 4, 40, '国战是影响航海世界局势的重要战斗，每个国民都应该参加战斗为国效力', 20, 70);
INSERT INTO `cfg_npc` VALUES (153, 4, 40, '在国战中可以获得大量的声望、经验和金钱，是让个人更强大的绝佳机会', 20, 70);
INSERT INTO `cfg_npc` VALUES (154, 4, 40, '国战分为四节，每次持续30分钟，谁先到1800分或者在结束时分数更多就能胜利', 20, 70);
INSERT INTO `cfg_npc` VALUES (155, 4, 40, '无论是新兵还是国王，在国战中都能造成伤害都能发挥强大的作用', 20, 70);
INSERT INTO `cfg_npc` VALUES (156, 4, 40, '别小看我们新兵的作用，蚂蚁咬死大象是国战中经常发生的情景！', 20, 70);
INSERT INTO `cfg_npc` VALUES (157, 4, 40, '国战比的就是双方战士造成的总伤害，在战场伤害超过一半的一方就会持续得分！', 20, 70);
INSERT INTO `cfg_npc` VALUES (158, 4, 40, '战场中最厉害的就是造成总伤害量最高的MVP！会获得巨大的荣誉和奖励！', 20, 70);
INSERT INTO `cfg_npc` VALUES (159, 4, 40, '要想在国战中获胜，指挥者的艺术和战士们的团结协作是关键', 20, 70);
INSERT INTO `cfg_npc` VALUES (160, 4, 40, '一旦国战开始，我们城市的投资工作就会完全暂停', 20, 70);
INSERT INTO `cfg_npc` VALUES (161, 4, 40, '国战结束后，胜利方将获得半个小时的受保护的自由投资时间，一定要抓紧机会', 20, 70);
INSERT INTO `cfg_npc` VALUES (162, 4, 40, '如果你在一个有实力的公会中,可以考虑去参加都市争夺战哦', 10, 70);
INSERT INTO `cfg_npc` VALUES (163, 4, 40, '在都市争夺战中胜利的公会成员会获得一个持续性的强力BUFF!', 10, 70);
INSERT INTO `cfg_npc` VALUES (164, 4, 40, '都市争夺战是公会之间争夺本国首都所有权的战斗!', 10, 70);
INSERT INTO `cfg_npc` VALUES (165, 5, 51, '船长，你想对我们的舰队做什么操作？我随时为您服务！', 0, 70);
INSERT INTO `cfg_npc` VALUES (166, 5, 51, '舰队船只的数量由好友数决定，但是不同等级有不同的船只上限限定哦', 0, 70);
INSERT INTO `cfg_npc` VALUES (167, 5, 51, '每个好友加入你的团队后都会默认为商船船只的', 0, 70);
INSERT INTO `cfg_npc` VALUES (168, 5, 51, '商船船只的数量决定了货运量的多少，每只商船运输能力固定为1', 0, 70);
INSERT INTO `cfg_npc` VALUES (169, 5, 51, '战斗船只数量决定你可以配备多少个船只和大炮，会极大的影响战斗力', 0, 70);
INSERT INTO `cfg_npc` VALUES (170, 5, 51, '要是我们的舰船和大炮比战斗船只还多就不起作用啦，多余的装备不算数呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (171, 5, 51, '每次改造舰船会消耗一定的金币和体力，要根据自己的需要慎重操作哦', 0, 70);
INSERT INTO `cfg_npc` VALUES (172, 5, 52, '每个人的舰队都由旗舰和普通舰船组成，只有旗舰才能配备撞角这些配件！', 0, 70);
INSERT INTO `cfg_npc` VALUES (173, 5, 52, '舰队里旗舰之外的普通舰船只能自动配备一个船体和大炮，而且还不能发挥特效', 0, 70);
INSERT INTO `cfg_npc` VALUES (174, 5, 52, '每个人都有一艘能装备配件的旗舰，旗舰的攻防会被放大十倍，所以你懂的！', 0, 70);
INSERT INTO `cfg_npc` VALUES (175, 5, 52, '旗舰的配件除了船体和武器之外，还能配备船帆、撞角、船首像和装甲', 0, 70);
INSERT INTO `cfg_npc` VALUES (176, 5, 52, '四星和五星的装备会有特殊技能，不过只有配备到旗舰上才能发挥作用！', 0, 70);
INSERT INTO `cfg_npc` VALUES (177, 5, 52, '一个四星五星技能组合好多装备也许比传奇装备还要厉害呢', 15, 70);
INSERT INTO `cfg_npc` VALUES (178, 5, 52, '同一种装备攻击防御也可能会出现不一样的地方，当然，数值越高越厉害', 15, 70);
INSERT INTO `cfg_npc` VALUES (179, 5, 53, '如果说装备是舰队的身躯，那么副官就是舰队真正的灵魂，灵魂强才是真的强！', 0, 70);
INSERT INTO `cfg_npc` VALUES (180, 5, 53, '副官上任后，会分别提升体力上限、回复速度，攻防还有交易、制造的能力！', 0, 70);
INSERT INTO `cfg_npc` VALUES (181, 5, 53, '旗舰可以配备六个不同职位的副官，尽早配备齐会极大的提升战斗力', 0, 70);
INSERT INTO `cfg_npc` VALUES (182, 5, 53, '副官都有后勤，军事，商业三种属性哦，要记得根据他们的特点分配职务', 0, 70);
INSERT INTO `cfg_npc` VALUES (183, 5, 53, '对副官里的航海长和仓管员来说，后勤就是最重要的属性', 0, 70);
INSERT INTO `cfg_npc` VALUES (184, 5, 53, '水兵长和炮击手是主管攻击和防御的，当然要选战斗属性高的！', 0, 70);
INSERT INTO `cfg_npc` VALUES (185, 5, 53, '要想赚钱多制造东西快，在会计师和工匠师位置上必须放上商业属性高的', 0, 70);
INSERT INTO `cfg_npc` VALUES (186, 5, 53, '副官会分为1星到5星。星级越高意味着副官的总属性点和成长性范围越高', 0, 70);
INSERT INTO `cfg_npc` VALUES (187, 5, 53, '别光迷恋总属性高的副官，全能不如特长！', 0, 70);
INSERT INTO `cfg_npc` VALUES (188, 5, 53, '副官就职后会不断的成长升级，升级增加点数多少就是由成长性决定的', 0, 70);
INSERT INTO `cfg_npc` VALUES (189, 5, 53, '副官到达四星后就会带特殊技能了！这个时候才是最强的！', 0, 70);
INSERT INTO `cfg_npc` VALUES (190, 5, 53, '传奇副官意味着强悍的当前，而一些低星级副官很可能具备更高的潜力！', 0, 70);
INSERT INTO `cfg_npc` VALUES (191, 5, 53, '最好的副官？当然是成长高，单项属性高！', 0, 70);
INSERT INTO `cfg_npc` VALUES (192, 5, 53, '招募好副官后一定要记得进入“舰队-部署”去安置好副官哦！', 0, 70);
INSERT INTO `cfg_npc` VALUES (193, 5, 53, '每个人最多有10名替补副官。招募的时候满了的话可以先去舰队解雇掉不合格的。', 0, 70);
INSERT INTO `cfg_npc` VALUES (194, 5, 53, '不同位置的副官会根据你不停的行动而得到经验', 0, 70);
INSERT INTO `cfg_npc` VALUES (195, 5, 53, '我们在航行的时候，航海长和仓管员位置上的副官都会得到相应的经验', 0, 70);
INSERT INTO `cfg_npc` VALUES (196, 5, 53, '如果我们经常战斗，那么水兵长和炮击手位置上的副官会成长非常快', 0, 70);
INSERT INTO `cfg_npc` VALUES (197, 5, 53, '做交易会让会计师副官得到经验，制造物品当然就会让工匠师副官得到经验啦', 0, 70);
INSERT INTO `cfg_npc` VALUES (198, 5, 53, '不要轻易的调整副官位置，调整不但会消耗体力，副官上任也许还会损失经验', 0, 70);
INSERT INTO `cfg_npc` VALUES (199, 5, 53, '当副官上任的位置和之前的位置不同时，会损失掉当前等级一半经验的！', 5, 70);
INSERT INTO `cfg_npc` VALUES (200, 5, 53, '副官到一定级别后就可以转职了，每次转职都能再随机获得一个技能', 25, 70);
INSERT INTO `cfg_npc` VALUES (201, 5, 53, '零转副官最高50级，一转后可以到60级，二转后就可以到达最高的70级', 25, 70);
INSERT INTO `cfg_npc` VALUES (202, 5, 53, '副官的技能一定程度上也会决定这个副官的作用', 25, 70);
INSERT INTO `cfg_npc` VALUES (203, 5, 53, '面临大战时，临时强化副官属性会是非常好的办法', 25, 70);
INSERT INTO `cfg_npc` VALUES (204, 6, 60, '您好，有什么能为您服务？钱存在银行才是最安全保险的！', 0, 70);
INSERT INTO `cfg_npc` VALUES (205, 6, 60, '不同的等级我们为你提供不同的银行存款额度', 0, 70);
INSERT INTO `cfg_npc` VALUES (206, 6, 60, '每次存款不要把钱全存了，身上没现金交易、战斗、补给都无法进行呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (207, 6, 60, '每次存款银行会收取2%的存款手续费哦，最低存款额度为100金币', 0, 70);
INSERT INTO `cfg_npc` VALUES (208, 6, 60, '每次取款不收任何的手续费，但是携带现金要注意安全哦', 0, 70);
INSERT INTO `cfg_npc` VALUES (209, 6, 60, '做任何交易将只从你的现金账户扣钱哦，如果现金不足需要来银行提款再进行消费', 0, 70);
INSERT INTO `cfg_npc` VALUES (210, 6, 60, '银行额度不够时您可以随时进行扩充，扩充是按照比例增长的呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (211, 6, 60, '扩充银行越早越好，因为是按比例扩张，早期扩充的限额后期会自动被放大的', 0, 70);
INSERT INTO `cfg_npc` VALUES (212, 7, 70, '您的所有固定资产全由我为您保管，我有什么可以为您服务么？', 0, 70);
INSERT INTO `cfg_npc` VALUES (213, 7, 70, '在存放物品的时候要注意您的仓库上限，到达上限后就无法继续存放啦', 0, 70);
INSERT INTO `cfg_npc` VALUES (214, 7, 70, '船上的商品只要卸载到仓库后才能变成可以用来拍卖和制造物品的原料', 0, 70);
INSERT INTO `cfg_npc` VALUES (215, 7, 70, '对于我来说，最刺激的就是每个人都可以成为伟大的锻造师、厨师或工艺师', 0, 70);
INSERT INTO `cfg_npc` VALUES (216, 7, 71, '厨师可以造出各种食物，让自己回复体力甚至拍卖赚取不菲的费用那', 10, 70);
INSERT INTO `cfg_npc` VALUES (217, 7, 71, '锻造师能够自己造船和武器，一个高级锻造师非常的受人尊敬', 0, 70);
INSERT INTO `cfg_npc` VALUES (218, 7, 71, '工艺师们都是多才多艺的，能够造旗舰必备的撞角、船帆、装甲和船首像', 0, 70);
INSERT INTO `cfg_npc` VALUES (219, 7, 71, '制造的熟练度要靠制造物品来获得哦，不过个人等级低的时候是无法突破的', 0, 70);
INSERT INTO `cfg_npc` VALUES (220, 7, 71, '制造物品是需要各种不同的卷轴的，可以在战斗、航行时搜集，也可以直接购买', 0, 70);
INSERT INTO `cfg_npc` VALUES (221, 7, 71, '有些特殊强力装备的卷轴只能在副本得到哦,包括能直接制造出传奇装备的卷轴', 10, 70);
INSERT INTO `cfg_npc` VALUES (222, 7, 71, '制造物品除了卷轴外当然还要各个城市的商品作为原材料，这个可不容易', 0, 70);
INSERT INTO `cfg_npc` VALUES (223, 7, 71, '有些原材料在敌人势力范围内，这个时候我们只有想办法去拍卖所买一些了', 3, 70);
INSERT INTO `cfg_npc` VALUES (224, 7, 71, '制造物品时有时候也能造出高星级的物品，让人很期待啊', 5, 70);
INSERT INTO `cfg_npc` VALUES (225, 7, 71, '高星级的物品一般会比低星级物品好，不过有些低星级物品意味着更大的潜力！', 8, 70);
INSERT INTO `cfg_npc` VALUES (226, 7, 71, '一个专业的制造师必然会在旗舰上的工匠师位置配备超强的副官的', 10, 70);
INSERT INTO `cfg_npc` VALUES (227, 7, 71, '在旗舰工匠师位置上的副官是会根据副官商业属性来提升制造速度的', 10, 70);
INSERT INTO `cfg_npc` VALUES (228, 7, 71, '一个四星以上的好工匠师副官应该具有超强的商业属性和强悍的制造类技能！', 10, 70);
INSERT INTO `cfg_npc` VALUES (229, 7, 71, '制造师不止可以给自己制造物品，还可以去拍卖所接其他航海家们代工的活赚钱', 15, 70);
INSERT INTO `cfg_npc` VALUES (230, 7, 71, '造完东西后一定要记得回仓库-制造这里来取出，要不然浪费时间啦', 5, 70);
INSERT INTO `cfg_npc` VALUES (231, 7, 71, '我们最多同时制造一个物品，所以一定要做好时间计划提高效率', 10, 70);
INSERT INTO `cfg_npc` VALUES (232, 13, 131, '任务分为按照要求执行动作的引导任务和快速获得经验的故事类任务', 0, 70);
INSERT INTO `cfg_npc` VALUES (233, 13, 131, '来酒馆做任务是最安逸的升级和赚钱方法哦，还有机会得到各种物品', 0, 70);
INSERT INTO `cfg_npc` VALUES (234, 13, 131, '有些任务会一些装备要求哦，这个时候去交易所那买够相应数量就可以继续啦', 3, 70);
INSERT INTO `cfg_npc` VALUES (235, 13, 131, '每个任务做完后还可以重复做哦，不过做更高等级的任务一般会收益更高的', 3, 70);
INSERT INTO `cfg_npc` VALUES (236, 13, 131, '升到10,20,30,40,50,60,70这些级别能解锁新的任务，会有更多奖励更多经验', 5, 50);
INSERT INTO `cfg_npc` VALUES (237, 13, 131, '做任务每一步都会消耗体力得到经验，有时候还能换装备的收集品套件和物品', 0, 50);
INSERT INTO `cfg_npc` VALUES (238, 13, 131, '每次升级后体力都会回复到全满哦，稍微计算下做任务的体力分配会事半功倍', 0, 50);
INSERT INTO `cfg_npc` VALUES (239, 13, 131, '你可以在任意不同的城市继续你未完成的任务，所以不用担心丢失进度啦', 0, 50);
INSERT INTO `cfg_npc` VALUES (240, 13, 131, '选择不同的出生意味着你会有不同的航海经历和不同的主线冒险，去完成它吧！', 0, 30);
INSERT INTO `cfg_npc` VALUES (241, 13, 131, '跟着引导任务升级不但能得到各种物品奖励和经验还能学到很多技巧呢！', 0, 30);
INSERT INTO `cfg_npc` VALUES (242, 13, 131, '每个引导任务都会有一个等级上限，如果过了等级还没完成任务就会消失掉', 0, 40);
INSERT INTO `cfg_npc` VALUES (243, 13, 131, '每天都坚持完成日常任务可以极大的加快你的经验,声望和金币的获取速度哦!', 0, 70);
INSERT INTO `cfg_npc` VALUES (244, 13, 131, '完成当天的全部日常任务之后会获得额外的奖励和抽取神秘宝箱的机会哦!', 0, 70);
INSERT INTO `cfg_npc` VALUES (245, 13, 132, '知己知彼百战不殆，你可以随时过来找我打听最新的世界排行信息', 0, 70);
INSERT INTO `cfg_npc` VALUES (246, 13, 132, '我们会每天搜集世界排行信息，然后在晚上12点公布最新的排行榜哦', 10, 70);
INSERT INTO `cfg_npc` VALUES (247, 13, 132, '要加油哦，登上世界排行榜可是航海世界中无上的光荣', 10, 70);
INSERT INTO `cfg_npc` VALUES (248, 13, 130, '欢迎来到佣兵协会，我有什么可以为你服务的？', 0, 70);
INSERT INTO `cfg_npc` VALUES (249, 13, 133, '我们这为您提供最刺激赚钱和经验最快速最有趣的赏金通缉！', 0, 70);
INSERT INTO `cfg_npc` VALUES (250, 13, 133, '通缉分为官方通缉榜和私人通缉榜，选择合适的去完成吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (251, 13, 133, '如果你和某个人有了血海深仇，我们随时为你提供通缉刺杀服务', 0, 70);
INSERT INTO `cfg_npc` VALUES (252, 13, 133, '要通缉一个人最好先把他加入你的黑名单，然后到我们这登记就可以开始复仇了', 0, 70);
INSERT INTO `cfg_npc` VALUES (253, 13, 133, '只要小小的一点手续费和足够打动赏金猎人们的钱，就能让你的仇人生不如死！', 0, 70);
INSERT INTO `cfg_npc` VALUES (254, 13, 133, '在不同的国家势力城市会看不到不同的通缉榜，中立城市能看到全部通缉令', 0, 70);
INSERT INTO `cfg_npc` VALUES (255, 13, 133, '官方通缉令会在36小时更新一次，一般都通缉对本国造成巨大危害的海盗和叛国贼', 0, 70);
INSERT INTO `cfg_npc` VALUES (256, 13, 133, '完成官方通缉令不但能得到不菲的金钱还能提高声望呢', 0, 70);
INSERT INTO `cfg_npc` VALUES (257, 13, 133, '有些私人悬赏的通缉令甚至会有比官方通缉更高的悬赏金额，去试试完成吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (258, 13, 133, '一旦接受通缉令后，你可以随时来我这查询被通缉人的坐标，去干掉他吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (259, 13, 133, '所有的通缉，都必须找到目标人物，使用劫掠干掉他之后才算完成', 0, 70);
INSERT INTO `cfg_npc` VALUES (260, 13, 133, '完成通缉任务后千万记得在截至时间内来我这交任务领取赏钱，别白费功夫', 0, 70);
INSERT INTO `cfg_npc` VALUES (261, 13, 133, '想成为名扬天下的赏金猎人嘛，来吧，我们需要你这样的人才', 0, 70);
INSERT INTO `cfg_npc` VALUES (262, 8, 8, '只有我这里才提供最强大最神秘的装备和道具，欢迎光临，我的客人', 0, 70);
INSERT INTO `cfg_npc` VALUES (263, 8, 8, '黑市？哈哈，那都是谣传。其实我白的很，非常的白～你看，对嘛？', 0, 70);
INSERT INTO `cfg_npc` VALUES (264, 8, 8, '我这里偶尔会有些打折货物，时刻保持关注，过期不候！', 0, 70);
INSERT INTO `cfg_npc` VALUES (265, 8, 8, '知道什么叫抢购么？就是我扔出100份便宜物品，然后等你们来抢～', 0, 70);
INSERT INTO `cfg_npc` VALUES (266, 5, 9, '船长，我感觉到这里充满了危险，一定要小心谨慎啊！', 0, 70);
INSERT INTO `cfg_npc` VALUES (267, 5, 9, '船长，我感觉今天会有大收获，哈哈，连我都开始兴奋了啊', 0, 70);
INSERT INTO `cfg_npc` VALUES (268, 5, 9, '老大，我发现我们遇到的探险情节跟区域有关，换区域就会看到不同的冒险', 0, 70);
INSERT INTO `cfg_npc` VALUES (269, 5, 9, '老大，探险真是刺激啊，每次都能拿到不少金币和宝物！', 0, 70);
INSERT INTO `cfg_npc` VALUES (270, 5, 9, '船长，干掉这些该死的拦路虎之后，能不能把抽宝箱的机会让给我？', 0, 70);
INSERT INTO `cfg_npc` VALUES (271, 5, 9, '船长，我是不是废话太多了？那我不说话了。一会还是让我来抽宝箱吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (272, 5, 9, '这个家伙我们每次打败他都变得更强了……', 0, 70);
INSERT INTO `cfg_npc` VALUES (273, 10, 10, '你有我没有但是我想要，是不是很烦？这正是我们存在的价值！', 0, 70);
INSERT INTO `cfg_npc` VALUES (274, 10, 10, '我最恨两种人:一种就是把自己国家战略物资倒卖给其他国家的，一种就是不做我生意的', 0, 70);
INSERT INTO `cfg_npc` VALUES (275, 10, 10, '我们这里接所有的拍卖，从原料到卷轴到装备到……好吧，暂时我们不搞人口贩卖', 0, 70);
INSERT INTO `cfg_npc` VALUES (276, 10, 10, '如果你有卷轴，有原料，却没有足够的熟练度来制造。这个时候你需要我们的代工服务！', 0, 70);
INSERT INTO `cfg_npc` VALUES (277, 10, 10, '如果你是一个制造大师，却没钱。很好，来我们这找个代工活赚个辛苦钱吧', 0, 70);
INSERT INTO `cfg_npc` VALUES (278, 10, 10, '买跨国的物资要小心，你会被额外收10%的关税', 10, 70);
INSERT INTO `cfg_npc` VALUES (279, 10, 10, '哈哈哈，有些信誉是零的小菜鸟也想来我这做生意，当我傻嘛？10级以后再说吧', 10, 70);
INSERT INTO `cfg_npc` VALUES (280, 10, 10, '你可以随时来我们这查看自己参与的拍卖信息，又快又全的信息服务，如何？', 10, 70);
INSERT INTO `cfg_npc` VALUES (281, 10, 10, '拍卖成功后，要记得来我这拿回自己的物品，别花了钱不拿东西', 0, 70);
INSERT INTO `cfg_npc` VALUES (282, 10, 10, '所有的商品得卸载到仓库才变成原来，才能开始拍卖出去', 0, 70);
INSERT INTO `cfg_npc` VALUES (283, 10, 10, '如何靠拍卖赚钱？把握市场动向，把握市场需求！最近流行什么来着？', 10, 70);
INSERT INTO `cfg_npc` VALUES (284, 11, 110, '欢迎来到最公正最爽快的海洋竞技场，天下所有的英雄都能在我这看到！', 0, 70);
INSERT INTO `cfg_npc` VALUES (285, 11, 110, '我们这分新手（1～14）初级(15~29) 中级（30～44）高级（45～69）顶级（70）5档', 0, 70);
INSERT INTO `cfg_npc` VALUES (286, 11, 110, '每天我们这都会产生一个冠军，胜者自然能拿到不少的奖励和声望', 0, 70);
INSERT INTO `cfg_npc` VALUES (287, 11, 110, '天梯赛是公平的，无论你现在体力、补给如何，一旦开战都会是按照满值计算', 0, 70);
INSERT INTO `cfg_npc` VALUES (288, 11, 110, '我们没有国籍和声望歧视。所有人都可以在这享受到公平的决斗！', 0, 70);
INSERT INTO `cfg_npc` VALUES (289, 11, 110, '即使你不能拿到冠军，仍然有机会在每一场战斗中赢得经验和金币', 0, 70);
INSERT INTO `cfg_npc` VALUES (290, 11, 110, '跨越时间空间，跨越种族和国籍的战斗，只会在我们这个伟大的竞技场发生', 0, 70);
INSERT INTO `cfg_npc` VALUES (291, 11, 110, '每天晚上23：30分，我们会结束当天的竞赛和发奖，并开始新一天的战斗', 0, 70);
INSERT INTO `cfg_npc` VALUES (292, 11, 110, '每天的天梯前20名都可以获得各种神秘的奖励!', 0, 70);
INSERT INTO `cfg_npc` VALUES (293, 11, 110, '挑战比自己排名高的当然得分更多，赢比自己排名低得分自然会减少', 0, 70);
INSERT INTO `cfg_npc_mission` VALUES (1, '加勒比海,西欧,地中海,华夏', 1, 9, 1, 1, 1, '黑市贩子', '哟，看起来很可靠的家伙，我的商会需要点{0}的{1}，有没有兴趣去帮我搞一点赚一笔……', '航行至 {0}，购买 {2} 船 {1}带回', '', 's,t', 30, '4=30,54=15', 1);
INSERT INTO `cfg_npc_mission` VALUES (2, '加勒比海,西欧,地中海,华夏', 1, 9, 2, 1, 2, '商会宣传员', '最近市场经济走势很差…很多职员转行当海盗了…令人悲伤！请去附近的{0}和{1}发放一些我们城市的商会联盟宣传单！', '在以下城市使用仓库中的商会传单：{0}，{1}', '', 't,b', 30, '4=30,52=15', 1);
INSERT INTO `cfg_npc_mission` VALUES (3, '南美,北欧,西亚,华夏', 1, 9, 3, 2, 3, '城市护卫', '经常有城外的人来求助，让我们帮他们清剿郊外的不法分子，但老实说我这里已经忙的焦头烂额了，去帮他们解决一些问题，我会给你报酬的！', '在1~9级的郊外完成 3 次支线任务', '1=3', 'n,e', 20, '4=30,53=15', 1);
INSERT INTO `cfg_npc_mission` VALUES (4, '加勒比海,西欧,地中海,华夏', 1, 9, 4, 2, 3, '城市护卫', '郊区越来越乱，盗贼，海贼，野兽……严重影响我周日和女友的郊游约会！去多做支线任务，清理掉那些烦人的野熊怪！', '在支线探险中击败5个野熊怪', '5', 'n,e', 20, '4=30,51=15', 1);
INSERT INTO `cfg_npc_mission` VALUES (5, '加勒比海,西欧,地中海,华夏', 1, 9, 5, 1, 4, '商会议员', '客人，不考虑投资我们商会么？不是我自夸哦，我们商会在每年的世界商会排行榜上都是名列前茅的哦！', '上交20000金币', '20000', 'p,b', 5, '71=1,40=3', 1);
INSERT INTO `cfg_npc_mission` VALUES (6, '南美,北欧,西亚,东亚', 10, 19, 1, 1, 5, '迷惘的店主', '最近市场变化太大了，市民每周的购买兴趣都变，烦死了！抱怨归抱怨，给我带点{0}的{1}过来，我会给你满意的报酬的。', '航行至 {0}，购买 {2} 船 {1}带回', '', 's,b', 30, '4=60,54=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (7, '南美,北欧,西亚,东亚', 10, 19, 2, 1, 6, '得意的商人', '听我说，上次评选世界前500商会时候，我们西兰花商上榜了哦~恩？你问具体名次，这可是秘密！帮我去{0}和{1}宣传下我们商会吧，报酬是这个价……', '在以下城市使用仓库中的商会传单：{0}，{1}', '', 't,p', 20, '4=60,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (8, '南美,北欧,西亚,东亚', 10, 19, 3, 2, 3, '城市护卫', '要说多少次啊，郊区还是很乱，严重影响咱和家人的郊游计划哦！继续去完成些支线任务，净化郊区，人人有责！', '在10~19级的郊外完成2次支线任务', '10=2', 'n,e', 20, '4=60,51=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (9, '南美,北欧,西亚,东亚', 10, 19, 4, 2, 3, '城市护卫', '说真的，自从上次队长逼我吃了两口野熊怪的肉后，我就爱上那味道了。这周会举办一个烧烤宴，帮我多带回点野熊怪肉回来！', '在支线探险中击败5个野熊怪', '5', 'n,e', 20, '4=60,51=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (10, '南美,北欧,西亚,东亚', 10, 19, 9, 3, 7, '求救的少女', '救命，救命啊！路过的船长大人，因为我父亲欠他们钱，他们就想逼我嫁给他们老板，被我拒绝后就上门抢人了！', '战斗胜利', '{\"flag\":{\"h\":4500,\"a\":2400,\"d\":1000},\"ship\":{\"h\":1500,\"a\":800,\"d\":300}}', 't,b', 0, '72=1,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (11, '南美,北欧,西亚,东亚', 10, 19, 8, 2, 8, '竞技场侍从', '你要和我打赌么，我觉得你这小身板在竞技场中时活不下去的~好，既然你不服气，就去赢几场给我看看，赌注在这就看你能不能拿到了！', '竞技场中挑战对手取得3场胜利', '3', 'b,n', 15, '4=60,53=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (12, '南美,北欧,西亚,东亚', 10, 19, 5, 4, 9, '巡游的城主', '我们城市崇尚自由与和平，发展前景一片光明！这位船长看起来如此威猛，想必一定会有一种投资我们城市的冲动吧，作为城主我会给你丰厚的回报哦~', '上交50000金币', '50000', 'p,n', 5, '45=1,40=3', 1);
INSERT INTO `cfg_npc_mission` VALUES (13, '南美,北欧,西亚,东亚', 10, 19, 7, 5, 10, '水晶学者', '这一期的学士周刊的主打文章是一篇关于{0}能量方面的研究论文，但不小心把珍贵的研究用{0}给弄丢了，可以的话，请给我带一些过来！', '上交100 {0}', '100', 'e,s', 5, '46=1', 1);
INSERT INTO `cfg_npc_mission` VALUES (14, '中美,南欧,东欧,东南亚', 20, 29, 1, 1, 11, '疲惫的商人', '投机的生意全部都失败了，这次我想进点稳赚不赔的小盈利货物，帮我运回一些{0}的{1}，放心，我剩下的钱足够支付你的跑腿费。', '航行至 {0}，购买 {2}船 {1}带回', '', 's,b', 30, '4=90,54=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (15, '中美,南欧,东欧,东南亚', 20, 29, 2, 1, 2, '商会宣传员', '知名度就是一个商会的生命！请去附近的{0}，{1}发放一些我的商会传单！也请答应我不要接受其他商会的相同请求，我会多付你一些工资的。', '在以下城市使用仓库中的商会传单：{0}，{1}', '', 't,p', 25, '4=90,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (16, '中美,南欧,东欧,东南亚', 20, 29, 3, 2, 3, '城市护卫', '这篇海域比我家乡那糟糕的环境还要差！附近的劫匪和怪物太多了，害的商人们都不敢走平时的道路。去清理些支线任务，至少要为我们打通一条安全的航线！', '在20~29级的郊外完成 2 次支线任务', '20=2', 'n,e', 25, '4=90,51=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (17, '中美,南欧,东欧,东南亚', 20, 29, 9, 4, 12, '高傲的船长', '你撞到我了，混蛋！需要我教教你这没教养的家伙怎么做人么！我可是很擅长用火枪崩死你这种渣滓的！现在跪下的话，我还可以饶你一命！', '战斗胜利', '{\"flag\":{\"h\":8000,\"a\":4000,\"d\":1800},\"ship\":{\"h\":3500,\"a\":1500,\"d\":500}}', 't,b', 0, '73=1,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (18, '中美,南欧,东欧,东南亚', 20, 29, 8, 4, 13, '竞技场观众', '这里的竞技场强度远超你以前所在的城市！看，刚才路过的就是那号称一顿饭吃掉两只野熊怪的 【队长】！看起来你不怎么相信我所说的话，这样的话，你亲自去验证一下如何？', '竞技场中挑战对手取得3场胜利', '3', 'b,n', 15, '4=90,53=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (19, '中美,南欧,东欧,东南亚', 20, 29, 5, 4, 9, '巡游的城主', '我们城市崇尚自由与和平，发展前景一片光明！这位船长看起来如此威猛，想必一定会有一种投资我们城市的冲动吧，作为城主我会给你丰厚的回报哦~', '上交200000金币', '200000', 'p,n', 5, '45=2,40=2', 1);
INSERT INTO `cfg_npc_mission` VALUES (20, '中美,南欧,东欧,东南亚', 20, 29, 6, 5, 14, '疯癫的研究员', '星钻！我需要大量的星钻！我会用我的宝物和你交换的，来换吧，你一定会和我换的对吧，快来跟我换！', '上交100 星钻', '100', 'e,b', 3, '41=1,53=50', 1);
INSERT INTO `cfg_npc_mission` VALUES (21, '中美,南欧,东欧,东南亚', 20, 29, 7, 5, 10, '水晶学者', '这一期的学士周刊的主打文章是一篇关于{0}能量方面的研究论文，但不小心把珍贵的研究用{0}给弄丢了，可以的话，请给我带一些过来！', '上交120 {0}', '120', 'e,s', 5, '46=1', 1);
INSERT INTO `cfg_npc_mission` VALUES (22, '北非,南亚', 30, 39, 1, 1, 1, '黑市贩子', '前几天其他国家的商人路过时透露了个秘密消息，{0}的{1}据说有机会大赚一笔，虽然不知道消息的真实性如何，但试试总是没错的。帮我运点过来吧！', '航行至 {0}，购买 {2}船 {1}带回', '', 's,b', 30, '4=120,54=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (23, '北非,南亚', 30, 39, 2, 1, 15, '忧心的商人', '征税，税，税！税率越来越高，这片地方也越来月不安全了，最近做梦都会感觉到要有战争发生了！离开这里之前，试试能不能再招一些人吧，去{0}，{1}发放一些的商会传单！', '在以下城市使用仓库中的商会传单：{0}，{1}', '', 't,p', 25, '4=120,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (24, '北非,南亚', 30, 39, 3, 2, 16, '愤怒的守卫', '我的孩子上个月被野外的山贼掳走！我恨所有这片海域的匪徒！帮我去杀光他们，我会给你所有你想要的！', '在30~39级的郊外完成 2 次支线任务', '30=2', 'n,e', 25, '4=120,51=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (25, '北非,南亚', 30, 39, 9, 4, 17, '路人男子', '喂，没错，就是叫你呢，别看别人！你看起来有点实力的样子，做个交易怎么样？帮我去教训一下那边的那男的，什么，你问我理由？他是我情敌！', '战斗胜利', '{\"flag\":{\"h\":18000,\"a\":6000,\"d\":3300},\"ship\":{\"h\":6000,\"a\":2000,\"d\":800}}', 't,b', 0, '76=1,52=20', 1);
INSERT INTO `cfg_npc_mission` VALUES (26, '北非,南亚', 30, 39, 8, 4, 18, '竞技场推广员', '竞技场之月开始了~取得一场胜利的送爆米花一桶，两场胜利的送海鱼亭的免费用餐券一张，三场胜利的奖品保密~有信心的话不如去试试？', '竞技场中挑战对手取得3场胜利', '3', 'b,n', 15, '4=120,53=20', 1);
